# frozen_string_literal: true

require 'rails_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to test the controller code that
# was generated by Rails when you ran the scaffold generator.
#
# It assumes that the implementation code is generated by the rails scaffold
# generator. If you are using any extension libraries to generate different
# controller code, this generated spec may or may not pass.
#
# It only uses APIs available in rails and/or rspec-rails. There are a number
# of tools you can use to make these specs even more expressive, but we're
# sticking to rails and rspec-rails APIs to keep things simple and stable.

RSpec.describe "/dashboard/activity_report_application_forms", type: :request do
  include Warden::Test::Helpers

  let(:user) { create(:user) }
  let(:other_user) { create(:user) }
  let(:certification_requirements) do
    build(
      :certification_certification_requirements,
      certification_date: Date.new(2025, 10, 31),
      number_of_months_to_certify: 1,
      months_that_can_be_certified: [ "2025-10-01", "2025-09-01", "2025-08-01" ]
    )
  end
  let(:certification) { create(:certification, certification_requirements:) }
  let(:certification_case) { create(:certification_case, certification: certification) }

  # This should return the minimal set of attributes required to create a valid
  # ActivityReportApplicationForm. As you add validations to ActivityReportApplicationForm, be sure to
  # adjust the attributes here as well.
  let(:valid_request_attributes) do
    {
      reporting_periods: [ { year: 2025, month: 10 } ]
    }
  end

  let(:valid_db_attributes) do
    valid_request_attributes.merge!(
      {
        user_id: user.id,
        certification_case_id: certification_case.id
      }
    )
  end

  before do
    login_as user
  end

  after do
    Warden.test_reset!
  end

  describe "GET /show" do
    it "renders a successful response" do
      activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
      get activity_report_application_form_url(activity_report_application_form)
      expect(response).to be_successful
    end

    it "errors if not owning user" do
      login_as other_user
      activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
      get activity_report_application_form_url(activity_report_application_form)
      expect(response).to be_client_error
    end

    context "with ex_parte activities" do
      let(:member_id) { "MEMBER123" }
      let(:certification_with_member) do
        create(:certification,
          certification_requirements: certification_requirements,
          member_id: member_id
        )
      end
      let(:certification_case_with_member) do
        create(:certification_case, certification: certification_with_member)
      end
      let(:activity_report_with_member) do
        ActivityReportApplicationForm.create!(
          user_id: user.id,
          certification_case_id: certification_case_with_member.id,
          reporting_periods: [ { year: 2025, month: 10 } ]
        )
      end

      before do
        # Create ex_parte activities for the member
        create(:ex_parte_activity,
          member_id: member_id,
          category: "employment",
          hours: 40,
          period_start: Date.new(2025, 10, 1),
          period_end: Date.new(2025, 10, 15)
        )

        # Create a self-reported activity
        create(:work_activity,
          activity_report_application_form_id: activity_report_with_member.id,
          month: Date.new(2025, 10, 1),
          hours: 30,
          name: "Test Organization"
        )
      end

      it "displays both ex_parte and self-reported activities" do
        get activity_report_application_form_url(activity_report_with_member)

        expect(response).to be_successful
        expect(response.body).to include("Employment Activity")
        expect(response.body).to include("From the State")
        expect(response.body).to include("Self Reported")
        expect(response.body).to include("Test Organization")
      end
    end
  end

  describe "GET /new" do
    it "redirects to the dashboard with an error if no certification case is provided" do
      get new_activity_report_application_form_url
      expect(response).to redirect_to(dashboard_path)
      follow_redirect!
      expect(response.body).to include("Cannot create activity report without a certification case")
    end

    context "with reporting source set to 'reporting_app'" do
      before do
        allow(Rails.application.config).to receive(:reporting_source).and_return("reporting_app")
      end

      it "renders a successful response" do
        get new_activity_report_application_form_url(certification_case_id: certification_case.id)
        expect(response).to be_successful
      end
    end
  end

  describe "POST /create" do
    context "with invalid parameters" do
      before do
        create(
          :activity_report_application_form,
          certification_case_id: certification_case.id,
          user_id: user.id
        )
      end

      let(:invalid_params) do
        { activity_report_application_form: { certification_case_id: certification_case.id } }
      end

      it "does not create a duplicate exemption form with the same certification_case_id" do
        expect {
          post activity_report_application_forms_url, params: invalid_params
        }.not_to change(ActivityReportApplicationForm, :count)
      end

      it "redirects to dashboard with notice with duplicate certification_case_id" do
        post activity_report_application_forms_url(params: invalid_params)
        expect(response).to redirect_to(dashboard_path)
        follow_redirect!
        expect(response.body).to include("Certification case has already been taken")
      end
    end

    context "with valid parameters" do
      let(:valid_params) do
        { activity_report_application_form: { certification_case_id: certification_case.id } }
      end

      it "creates a new ActivityReportApplicationForm" do
        expect {
          post activity_report_application_forms_url, params: valid_params
        }.to change(ActivityReportApplicationForm, :count).by(1)
        expect(response).to have_http_status(:found)
      end

      it "redirects to choose reporting periods page" do
        post activity_report_application_forms_url, params: valid_params
        expect(response).to redirect_to(edit_activity_report_application_form_url(ActivityReportApplicationForm.last))
        follow_redirect!
        expect(response).to have_http_status(:ok)
        expect(response.body).to include("Select reporting period")
      end
    end
  end

  describe "GET /edit" do
    let(:activity_report_application_form) { ActivityReportApplicationForm.create! valid_db_attributes }

    it "renders a successful response" do
      get edit_activity_report_application_form_url(activity_report_application_form)
      expect(response).to be_successful
    end

    it "renders an error response for non-owning user" do
      login_as other_user

      get edit_activity_report_application_form_url(activity_report_application_form)
      expect(response).to be_client_error
    end
  end

  describe "GET /review" do
    it "renders a successful response" do
      activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
      get review_activity_report_application_form_url(activity_report_application_form)
      expect(response).to be_successful
    end
  end

  describe "PATCH /update" do
    context "with valid parameters" do
      let(:new_attributes) {
        {
          reporting_periods: [ { year: 2025, month: 8 }.to_json ]
        }
      }

      it "updates the requested activity_report_application_form" do
        activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
        patch activity_report_application_form_url(activity_report_application_form), params: { activity_report_application_form: new_attributes }
        expect(response).to have_http_status(:found)
        activity_report_application_form.reload
        expect(activity_report_application_form.reporting_periods).to eq([ Strata::YearMonth.new(year: 2025, month: 8) ])
      end

      it "does not update the requested activity_report_application_form if non-owning user" do
        login_as other_user

        activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
        activity_report_application_form_original = activity_report_application_form.dup

        patch activity_report_application_form_url(activity_report_application_form), params: { activity_report_application_form: new_attributes }

        # assert DB state is still in the previous state
        activity_report_application_form.reload

        ignored_attributes = [ "id", "created_at", "updated_at" ]
        expect(activity_report_application_form.attributes.except(*ignored_attributes))
          .to eq(activity_report_application_form_original.attributes.except(*ignored_attributes))

        expect(response).to be_client_error
      end

      it "redirects to the activity_report_application_form" do
        activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
        patch activity_report_application_form_url(activity_report_application_form), params: { activity_report_application_form: new_attributes }
        activity_report_application_form.reload
        expect(response).to redirect_to(activity_report_application_form_url(activity_report_application_form))
      end
    end

    context "with incorrect number of reporting periods" do
      let(:invalid_attributes) {
        {
          reporting_periods: [ { year: 2025, month: 8 }.to_json, { year: 2025, month: 9 }.to_json ]
        }
      }

      it "renders an unprocessable entity response" do
        activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
        patch activity_report_application_form_url(activity_report_application_form), params: { activity_report_application_form: invalid_attributes }
        expect(response).to have_http_status(:unprocessable_content)
      end
    end

    context "with reporting periods outside of allowed months" do
      let(:invalid_attributes) {
        {
          reporting_periods: [ { year: 2024, month: 5 }.to_json ]
        }
      }

      it "renders an unprocessable entity response" do
        activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
        patch activity_report_application_form_url(activity_report_application_form), params: { activity_report_application_form: invalid_attributes }
        expect(response).to have_http_status(:unprocessable_content)
      end
    end
  end

  describe "POST /submit" do
    let(:application_form) { ActivityReportApplicationForm.create! valid_db_attributes }

    it "marks the activity report as submitted" do
      post submit_activity_report_application_form_url(application_form)

      application_form.reload
      expect(application_form).to be_submitted
    end

    it "redirects to GET /show on success" do
      post submit_activity_report_application_form_url(application_form)

      expect(response).to redirect_to(activity_report_application_form_url(application_form))
    end

    it "sets the current step of the case to 'review_activity_report'" do
      post submit_activity_report_application_form_url(application_form)

      kase = CertificationCase.find_by(id: application_form.certification_case_id)
      expect(kase.business_process_instance.current_step).to eq("review_activity_report")
    end

    it "errors if not owning user" do
      login_as other_user

      post submit_activity_report_application_form_url(application_form)

      expect(response).to be_client_error
    end
  end

  describe "DELETE /destroy" do
    it "destroys the requested activity_report_application_form" do
      activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
      expect {
        delete activity_report_application_form_url(activity_report_application_form)
      }.to change(ActivityReportApplicationForm, :count).by(-1)
    end

    it "does not destroy the requested activity_report_application_form if non-owning user" do
      login_as other_user

      activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
      expect {
        delete activity_report_application_form_url(activity_report_application_form)
      }.not_to change(ActivityReportApplicationForm, :count)
    end

    it "redirects to the dashboard" do
      activity_report_application_form = ActivityReportApplicationForm.create! valid_db_attributes
      delete activity_report_application_form_url(activity_report_application_form)
      expect(response).to redirect_to(dashboard_path)
    end
  end
end
